import logo from './logo.svg';
import './App.css';
import {useState} from "react";
import {generateUniqueID} from "web-vitals/dist/modules/lib/generateUniqueID";
import ButtonsAndTasks from './ButtonsAndTasks';
import TaskItem from "./TaskItem";
import Alert from "./Alert";

let currentDeleteOption = "";


let data = [
  {
    id: 1,
    name: "test",
    check: false,
    hidden: false
  },
  {
    id: 2,
    name: "test2",
    check: false,
    hidden: false
  }
]


const dropdownOptions = {
  option1: "All Tasks",
  option2: "Completed Tasks",
  option3: "Uncompleted Tasks"
}

const menuItems = [
  {
    id: "view",
    name: "View"
  },
  {
    id: "trash",
    name: "ðŸ—‘",
  }
]

function App() {
  const [tasks, setTasks] = useState(data);
  const [showAlert, setShowAlert] = useState(false);

  function toggleModal() {
    setShowAlert(!showAlert);
  }

  function setCurrentDeleteOption(currDelete) {
    currentDeleteOption = currDelete;
  }

  function handleAlertOK() {
    console.log('ok');
  }

  function handleTaskNameChange(e, id) {
    console.log("handling task name change!")
    let taskIndex = tasks.findIndex(e => e.id === id);
    tasks[taskIndex].name = e.target.value
    console.log(tasks)
    setTasks(tasks);
  }

  function hideTask(task, check) {
    if (task.check === check) {
      task.hidden = true;
    }
  }

  function toggleCheckbox(id) {
    tasks.find(e => e.id === id).check = !tasks.find(e => e.id === id).check
  }

  function deleteOrView(id, option) {
    if (id === "trash") {
      if (option === "All Tasks") {
        setTasks([])
      } else if (option === "Completed Tasks") {
        let newTasks = tasks.filter(element => element.check === false)
        setTasks(newTasks)
      } else if (option === "Uncompleted Tasks") {
        let newTasks = tasks.filter(element => element.check === true)
        setTasks(newTasks)
      }
    }
    if (id === "view") {
      console.log("tasks before", tasks)
      console.log(option)
      if (option === "All Tasks") {
        tasks.forEach(e => e.hidden = false)
        setTasks(tasks);
      } else if (option === "Completed Tasks") {
        tasks.forEach(e => hideTask(e, false))
        setTasks(tasks);
        console.log("tasks ", tasks)
        console.log("in completed")
       } else if (option === "Uncompleted Tasks") {
        tasks.forEach(e => hideTask(e, true))
        setTasks(tasks);
        console.log("tasks ", tasks)
        console.log("in uncompleted")

      }
    }
  }

  function MakeNewItem() {
    setTasks([      {
      id: generateUniqueID,
      name: "Enter Task",
      checked: false,
      hidden: false
    },
      ...tasks])
  }

  return (
      <div className="App">
        <div id="app-title"><h2>Tasks</h2>
        </div>
        {showAlert && <Alert onClose={toggleModal} onOK={deleteOrView("trash", currentDeleteOption)} dropdownOptions={dropdownOptions}>
          <div>
            Are you sure you want to delete these tasks?
          </div>
        </Alert>}
        <ButtonsAndTasks setDeleteOption={setCurrentDeleteOption} toggleModal={toggleModal} handleAlertOK={handleAlertOK} handleTaskNameChange={handleTaskNameChange} toggleCheckbox={toggleCheckbox} deleteOrView={deleteOrView} tasksData={tasks} buttonData={menuItems} dropdownOptions={dropdownOptions} makeNewItem={MakeNewItem}/>
      </div>
  );
}

export default App;
