import logo from './logo.svg';
import './App.css';
import {useState} from "react";
import {generateUniqueID} from "web-vitals/dist/modules/lib/generateUniqueID";
import ButtonsAndTasks from './ButtonsAndTasks';



let data = [
  {
    id: 1,
    name: "test",
    check: false
  },
  {
    id: 2,
    name: "test2",
    check: true
  }
]


const dropdownOptions = {
  option1: "All Tasks",
  option2: "Completed Tasks",
  option3: "Uncompleted Tasks"
}

const menuItems = [
  {
    id: "view",
    name: "View"
  },
  {
    id: "trash",
    name: "ðŸ—‘",
  }
]

function App() {
  const [tasks, setTasks] = useState(data);
  const [checked, setChecked] = useState(tasks.filter(element => element.check === true));

  function DeleteOrView(id, option) {
    if (id === "trash") {
      if (option === "All Tasks") {
        setTasks([])
      } else if (option === "Completed Tasks") {
        setTasks(tasks.filter(element => element.check === true))
      } else if (option === "Uncompleted Tasks") {
        setTasks(tasks.filter(element => element.check === false))
      }
    }
  }

  function MakeNewItem() {
    setTasks([      {
      id: generateUniqueID,
      name: "",
      checked: false
    },
      ...tasks])
  }

  return (
      <div className="App">
        <div id="app-title"><h2>Tasks</h2>
        </div>
        <ButtonsAndTasks deleteOrView={DeleteOrView} tasksData={tasks} buttonData={menuItems} dropdownOptions={dropdownOptions} makeNewItem={MakeNewItem}/>
      </div>
  );
}

export default App;
