import TaskContainer from './TaskContainer';
import DropdownButton from './DropdownButton';


function ButtonsAndTasks(props) {
    return <div className="buttons-and-tasks">
        <div className="menu-buttons-container">
            <div className="dropdown" id="new-item-button">
                <button type="button" className="menu-buttons" onClick={props.makeNewItem}>New Item</button>
            </div>
            <div className="dropdown" id="view-button">
                <button className="menu-buttons">{props.name}<span className="small-triangle"> â–¼ </span></button>
                <div className="dropdown-content">
                    {/*{props.deleteOrView(props.id, props.options.option1),*/}
                    <button onClick={() => console.log("hi")}>{props.dropdownOptions.option1}</button>
                    <button onClick={props.deleteOrView(props.id, props.dropdownOptions.option2)}>{props.dropdownOptions.option2}</button>
                    <button onClick={props.deleteOrView(props.id, props.dropdownOptions.option3)}>{props.dropdownOptions.option3}</button>
                </div>
            </div>
            {/*{props.buttonData.map(e => <DropdownButton name={e.name} options={props.dropdownOptions} deleteOrView={props.deleteOrView}/>)}*/}
        </div>
        <TaskContainer tasksData={props.tasksData}/>
    </div>
}

export default ButtonsAndTasks;
